void RoussetDemoViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &largeVideoCloseButton)
    {
        //CloseLargeVideo
        //When largeVideoCloseButton clicked call virtual function
        //Call closeLargeVideo
        closeLargeVideo();
    }
}

void RoussetDemoViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &coverflowFlexButton)
    {
        //OpenCoverflow
        //When coverflowFlexButton clicked call virtual function
        //Call coverflowClickHandler
        coverflowClickHandler();
    }
    if (&src == &videoFlexButton)
    {
        //OpenLargeVideo
        //When videoFlexButton clicked call virtual function
        //Call videoClickHandler
        videoClickHandler();
    }
    if (&src == &groundhogFlexButton)
    {
        //ActivateGroundhog
        //When groundhogFlexButton clicked call virtual function
        //Call groundhogClickHandler
        groundhogClickHandler();
    }
    if (&src == &pineconeFlexButton)
    {
        //ActivatePinecone
        //When pineconeFlexButton clicked call virtual function
        //Call pineconeClickHandler
        pineconeClickHandler();
    }
    if (&src == &carFlexButton)
    {
        //ToogleCarLights
        //When carFlexButton clicked call virtual function
        //Call carClickHandler
        carClickHandler();
    }
    if (&src == &backButton)
    {
        //ChangeScreenToMain
        //When backButton clicked change screen to MenuLauncherScreen
        //Go to MenuLauncherScreen with screen transition towards West
        application().gotoMenuLauncherScreenScreenWipeTransitionWest();
    }
}
